import socketsock = socket.socket()sock.bind(('192.168.3.156', 3030))sock.listen(2)while True:    conn, addr = sock.accept()    data = conn.recv(1024)    print(dict(data.decode()))    nosock = socket.socket()    print('1')    nosock.connect(('192.168.3.57', 3030))    print('2')    nosock.send(data.upper())    print('3')conn.close()nosock.close()